import{_ as t,c as r,j as e,a as o,o as l}from"./chunks/framework.DKq7WeZY.js";const N=JSON.parse('{"title":"1. 摘要","description":"","frontmatter":{"sidebarDepth":2},"headers":[],"relativePath":"algorithm/MobileGNSS-SPP/01-overview.md","filePath":"algorithm/MobileGNSS-SPP/01-overview.md"}'),i={name:"algorithm/MobileGNSS-SPP/01-overview.md"};function n(S,a,s,d,p,c){return l(),r("div",null,a[0]||(a[0]=[e("h1",{id:"_1-摘要",tabindex:"-1"},[o("1. 摘要 "),e("a",{class:"header-anchor",href:"#_1-摘要","aria-label":'Permalink to "1. 摘要"'},"​")],-1),e("p",null,"本文主要介绍了 MobileGNSS-SPP 项目中的工程经验，主要包括数据分析与GNSS算法优化。该项目尤其针对某款 GNSS 芯片进行了定制优化，旨在提升其定位性能，不过其中用到的方法也可以迁移到其他芯片平台上。",-1),e("p",null,"在数据分析方面，详细探讨了数据采集方式、C/N0（载波噪声密度比）水平、观测数据间的相关性以及伪距和多普勒之间的相关性等关键指标。通过这些分析，对芯片的性能和数据特点有了更深入的了解。",-1),e("p",null,"在算法优化方面，重点介绍了加权最小二乘（RWLS）和扩展卡尔曼滤波（EKF）两种方法。对于 RWLS，深入探讨了 SNR 加权模型、速度估计中的加权以及 M 估计（抗差估计）。对于 EKF，详细阐述了基于 EKF 的观测域滤波过程，包括时间更新、量测更新以及工程实现，并探讨了零速修正（ZUPT）和 在EKF 中使用 RWLS 进行M估计的相关问题。通过这些方法的应用，显著提升了 GNSS 芯片的定位精度和性能表现。",-1),e("p",null,"最后，本文还介绍一种基于二次规划的优化方法，用于后处理解算。",-1)]))}const P=t(i,[["render",n]]);export{N as __pageData,P as default};
