import{_ as o,c as r,a6 as e,o as a}from"./chunks/framework.B5S16sG3.js";const p=JSON.parse('{"title":"1 摘要","description":"","frontmatter":{"sidebarDepth":2},"headers":[],"relativePath":"robot/Nav-Tools/01-overview.md","filePath":"robot/Nav-Tools/01-overview.md"}'),s={name:"robot/Nav-Tools/01-overview.md"};function l(d,t,i,n,h,c){return a(),r("div",null,t[0]||(t[0]=[e('<h1 id="_1-摘要" tabindex="-1">1 摘要 <a class="header-anchor" href="#_1-摘要" aria-label="Permalink to &quot;1 摘要&quot;">​</a></h1><br><img src="https://raw.githubusercontent.com/salmoshu/Winchell-ImgBed/main/img/20251020-145700.jpg"><p style="text-align:center;">图1-1: Nav-Tools 示意图</p><p>Nav-Tools 是一款专为机器人开发者打造的<strong>桌面级可视化工作台</strong>，全面覆盖机器人复合应用与主流传感器。目前已集成通用数据流 Flow、GNSS 定位模块、超声波避障、PID 跟随仿真和电机驱动等功能，支持布局与数据字段的灵活自定义，极大提升调试效率与开发体验。应用下载请查看：</p><p><a href="https://github.com/salmoshu/Nav-Tools/releases" target="_blank" rel="noreferrer">https://github.com/salmoshu/Nav-Tools/releases</a></p><h2 id="核心特性" tabindex="-1">核心特性 <a class="header-anchor" href="#核心特性" aria-label="Permalink to &quot;核心特性&quot;">​</a></h2><ul><li>🌱 <strong>简洁可扩展</strong>：目录结构清晰，支持快速添加新模块</li><li>🖥 <strong>多窗口支持</strong>：轻松实现多窗口并行调试</li><li>📊 <strong>实时可视化</strong>：支持时序图、轨迹图、控制台、仪表盘等多种展示方式</li><li>🧩 <strong>高度自定义</strong>：支持字段扩展、滑窗、过滤、颜色配置、布局保存等高级功能</li></ul><h2 id="支持模块与应用" tabindex="-1">支持模块与应用 <a class="header-anchor" href="#支持模块与应用" aria-label="Permalink to &quot;支持模块与应用&quot;">​</a></h2><table><thead><tr><th>应用</th><th>模块名称</th><th>功能描述</th></tr></thead><tbody><tr><td>ROBOT</td><td>Flow</td><td>通用数据流可视化（时序图、轨迹图、控制台）</td></tr><tr><td>PERC</td><td>Ultrasonic</td><td>超声波避障数据可视化（滤波、障碍物检测）</td></tr><tr><td>POS</td><td>GNSS</td><td>卫星定位模块（轨迹、信号、星空图）</td></tr><tr><td rowspan="2">PNC</td><td>Motor</td><td>电机驱动的控制下发与数据可视化</td></tr><tr><td>FollowSim</td><td>PID 跟随仿真（仪表盘、速度曲线、参数配置）</td></tr></tbody></table>',10)]))}const g=o(s,[["render",l]]);export{p as __pageData,g as default};
